void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv.x *=iResolution.x/iResolution.y;
    uv.x += smoothstep(0., 1., abs(sin(iTime/8.)));
    uv.y += smoothstep(0., 1., abs(cos(iTime/10.)));

    uv *= 3.;
    vec2 i_uv = floor(uv);
    vec2 f_uv = fract(uv);
    vec3 color = vec3(0.);
    float t4dots = 32.+12.*-cos(iTime);
    float t2dots = 11. + iTime;

    color = vec3(smoothstep(0.8,0.9, abs(sin(f_uv.y*t4dots)*cos(f_uv.x*t4dots))));

    color *= vec3(smoothstep(0.8, 0.9,abs(sin(f_uv.x*t4dots/10.)*cos(f_uv.y*t4dots/10.))));
    color *= vec3(abs(sin(iTime+uv.x)), abs(sin(iTime+uv.y)), abs(sin(iTime)));

	fragColor = vec4(color,1.0);
}
