/* shader:  Riemann_Zeta_HV.glsl    2018-10-23

Fork of https://www.shadertoy.com/view/Ms2fWR

The Riemann zeta function is one of the most legendary objects 
in mathematics. For reasons I don't understand, proving facts 
about it is very hard, as it encodes crucial arithmetic information 
from the ring of integers of the field of complex numbers.

tags: zeta, riemann, function, 2d, horizontal, view
*/

#define ITERATIONS 256.
#define hsv2rgb(v) abs(fract(v + vec3(3, 2, 1) / 3.) - .5) * 6. - 1.

// Wikipedia  https://en.wikipedia.org/wiki/Riemann_zeta_function
vec2 riemann_zeta_series(vec2 z)
{
  vec2 sum = vec2(0);
  for(float i = 1.; i < ITERATIONS; ++i)
    sum += sin(-z.y * log(i) - vec2(1.57, 0.)) / pow(i, z.x);
  return sum;
}

void mainImage(out vec4 fragColor,  in vec2 fragCoord)
{
  float zoom = (iMouse.z > 0.) ? 4.1 : 1.0;

  vec2 uv = zoom*(2.*fragCoord - iResolution.xy) / iResolution.y;
  uv = 3. * vec2(1.5*uv.y-0.1, uv.x-2.);  

  if (iMouse.z > 0.)
    uv.y += iMouse.x*4.;
  else uv.y += +iTime*0.4;

  vec2 sum = riemann_zeta_series(uv);
  fragColor.rgb = hsv2rgb(atan(sum.y / sum.x));
}
