#define S(a,b,t) smoothstep(a,b,t)
#define sat(x) clamp(x, 0., 1.)

float remap01(float a, float b, float t){
	return sat((t-a)/(b-a));
}

float remap(float a, float b, float c, float d, float t){
    return sat((t-a)/(b-a)) * (d-c) + c;
}

vec2 within(vec2 uv, vec4 rect){
	return (uv-rect.xy) / (rect.zw-rect.xy); // Box Within
}

vec4 Eye(vec2 uv){
    uv -= .5;

    float d = length(uv);

    vec4 irisCol = vec4(.3, .5, 1., 1.); // Iris Color
    vec4 col = mix(vec4(1.), irisCol, S(.1, .7, d)*.5); // Eye Color

    col.rgb *= 1.- S(.45, .5, d), .5 * sat(-uv.y-uv.x); // Shadow 

    col.rgb = mix(col.rgb, vec3(0.), S(.3, .28, d)); // Outline of Iris

    irisCol.rgb *= 1. + S(.3, .05, d); // Gradient inside iris
    col.rgb = mix(col.rgb, irisCol.rgb, S(.28, .25, d)); // Inside Iris 

    col.rgb = mix(col.rgb, vec3(0.), S(.15, .14, d)); // Color

    //highlights
    float highlight = S(.1, .09, length(uv- vec2(-.15, .15))); // Top Highlight
    highlight += S(.07, .05, length(uv+ vec2(-.08, .08))); // Bottom Highlight
    col.rgb = mix(col.rgb, vec3(1.), highlight); // Mix 


    col.a = S(.5, .48, d); // Draw the Eye 

    return col; // Return the Eye
}

vec4 Mouth(vec2 uv){
    uv -= .5;
    vec4 col = vec4(.5, .18, .05, 1.);
    uv.y *= 1.5;
    uv.y -= uv.x*uv.x *2.;

    float d = length(uv);
    col.a = S(.5, .48, d);

    // Teeth
    float td = length(uv-vec2(0., .6));
    vec3 toothCol = vec3(1.) * S(.6, .35, d);
    col.rgb = mix(col.rgb, toothCol, S(.4, .37, td));

    // Tounge
    td = length(uv+vec2(0., .5));
    col.rgb = mix(col.rgb, vec3(1., .5, .5), S(.5, .2, td));

    return col; // Return the Mouth
}

vec4 Head(vec2 uv){
    vec4 col = vec4(.9, .65, .1, 1.); // Color Vector

    float d = length(uv);

    col.a = S(.5, .49, d);

    float edgeShade = remap01(.35, .5, d);
    edgeShade += edgeShade;

    col.rgb *= 1. - edgeShade *.5;
    col.rgb = mix(col.rgb, vec3(.6, .3, .1), S(.47, .48, d)); 

    float highlight = S(.41, .405, d)*.5;
    highlight *= remap(.41, .0, .75, 0., uv.y); // Face Gradient
    col.rgb = mix(col.rgb, vec3(1.), highlight);

    //cheek
    d = length(uv-vec2(.25, -.2)); // Location 
    float cheek = S(.2, .1, d)*.4; // Color
    cheek *= S(.18, .16, d); // Edge
    col.rgb = mix(col.rgb, vec3(1., .1, .1), cheek); // Mixes Color

    return col; // Returns Head 
}

vec4 Smiley(vec2 uv){
    vec4 col = vec4(0.);

    uv.x = abs(uv.x); // Mirror (two halves of the face) 

    vec4 head = Head(uv); // Draws the Head
    vec4 eye = Eye(within(uv, vec4(.03, -.1, .37, .25))); // Draws Eye
    vec4 mouth = Mouth(within(uv, vec4(-.3, -.4, .3, -.1))); // Draws Mouth

    col = mix(col, head, head.a); // Adds Color 
    col = mix(col, eye, eye.a); // Adds Color
    col = mix(col, mouth, mouth.a); // Adds Color


    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	uv -= .5;
    uv.x *= iResolution.x/iResolution.y;

    // Varies pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    fragColor =  Smiley(uv); // Put everything together.
}
