void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.st / iResolution.xy;

    vec3 bufA = texture(iChannel0, uv).rgb;
    vec3 bufB = texture(iChannel1, uv).rgb;
    vec3 bufC = texture(iChannel2, uv).rgb;
    vec3 bufD = texture(iChannel3, uv).rgb;

    vec3 bufBMap = step(1.0, bufA);
    vec3 bufCMap = (1.0-bufBMap) *  step(0.75, bufA);
    vec3 bufDMap = ((1.0-bufBMap) * 1.0-bufCMap) *  step(0.5, bufA);

   	vec3 color =vec3(bufBMap * bufB + bufCMap * bufC + bufDMap * bufD);
    //vec3 color =vec3(bufDMap * bufD);

    // Output to screen        
    fragColor = vec4(color, 1.0);
}

const float size = 20.0;
const float redScale   = 0.298912;
const float greenScale = 0.586611;
const float blueScale  = 0.114478;
const vec3  monochromeScale = vec3(redScale, greenScale, blueScale);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord.st / iResolution.xy;

    //  mosaic UV
    vec2 muv = floor(gl_FragCoord.st / size) * size / iResolution.xy;

    vec3 color = texture(iChannel0, muv).rgb;

    // monochro
    float monochro = dot(color.rgb, monochromeScale);

    if (monochro > 0.6){
         monochro = 1.0;
    }
    else if(monochro > 0.5){
        monochro = 0.75;
    }
    else if(monochro > 0.4){
        monochro = 0.5;
    }
    else if(monochro > 0.3){
        monochro = 0.0;
    }

    // Output to screen
    fragColor = vec4(vec3(monochro), 1.0);
}

const float size = 20.0;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.st / min(iResolution.x, iResolution.y);
    uv *= 14.4;
    uv = fract(uv);
	vec3 color = texture(iChannel0, uv).rgb;
    fragColor = vec4(color, 1.0);   
}

