vec2 cub_(float t,vec2 a,vec2 b){
    float ct=1.-t;
    return 3.*ct*ct*t*a+3.*ct*t*t*b+t*t*t;
}
float cub(vec2 a,vec2 b,float x){
    vec2 it=vec2(0.,1.);
    for (int i=0;i<10;i++) {
        float pos=(it.x+it.y)/2.;
        vec2 r=cub_(pos,a,b);
        if (r.x>x){
            it.y=pos;
        }else{
            it.x=pos;
        }
    }
    return cub_((it.x+it.y)/2.,a,b).y;
}
float smthstep(float a, float x)
{
 	return smoothstep(a, a + .003, x);   
}
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord/iResolution.yy;
    float x = uv.x - (iResolution.x - iResolution.y)/iResolution.x;
    float y = 1. - uv.y;
    vec3 col = vec3(0.);

    col += 1.
        *smthstep(.135+.015*(x-.7)/.07,y)
        *smthstep(y,.8+.05*(x-.3)/.04)
        *smthstep(.7-.4*cub(vec2(.52,.17),vec2(.85,.43),(y-.135)/.665),x)
        *smthstep(x,.77-.43*cub(vec2(.52,.17),vec2(.85,.43),(y-.15)/.7))
        ;

    for (int i = 0; i < 2; i++) {
        col += 1.
            *smthstep(.14-.02*(x-.38)/.06,y)
            *smthstep(y,.38-.02*(x-.46)/.06)
            *smthstep(.38+.08*cub(vec2(.28,.37),vec2(.61,.69),(y-.14)/.24),x)
            *smthstep(x,.44+.08*cub(vec2(.28,.37),vec2(.61,.69),(y-.12)/.24))
            ;
        x += .2;
        y -= .05;
    }

    fragColor = vec4(col,1.0);
}
