float test(float x, float y, float d, float t){
   	float factor = 1.;
    float c = 1.;
	for (float i = 0.; i < 5.; ++i)
    {
        float j = i*factor + factor+1.;
        vec2 a = vec2(fract(x*j + j*0.5) + t*j,
                       fract(y*j + j*.125 + 0.05/d)*.2 + t*1.12345*j)*10.;
        float b = length(fract(a)*2.-1.);
		c = min(c*3.,pow(b*1.1,1.1));
    }
    return c;
}

void mainImage( out vec4 f, in vec2 w ) {

	vec2 r = iResolution.xy;
    vec2 c = vec2(cos(iTime)*r.x, sin(iTime)*r.x) * 0.125 + vec2(0.5);
    vec2 p = w - vec2(r.x, r.y)*.5 + c;
	float d = length(p) / r.y * 100.;
    float x = 1./pow(d, 2.);
    d += sin(d)*2.5 + 2.5;
    float y = (atan(p.x, p.y)*0.5+1.57) / 3.14;
	float t = iTime*0.05;
    float _r = test(x * d*0.25, y, d, t);
    float _g = test(x*3. * d*0.25, y*3., d, t*2.);
    float _b = test(x*7. * d*0.25, y*7., d, t*0.5);

	f = vec4(_r,_g,_b,1);
}
