void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = texture(iChannel0, uv).rgb;

    fragColor = vec4(col,1.0);
}

vec4 cell(vec2 fragCoord, vec2 pixel)
{
	vec2 uv = (fragCoord-pixel) / iResolution.xy;
    return texture(iChannel0, uv);
}

//based on Suture Fluid by cornusammonis : https://www.shadertoy.com/view/XddSRX

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = gl_FragCoord.xy / iResolution.xy;
	vec2 uvc = gl_FragCoord.xy / iResolution.xy;

    if (iTime < 0.1) // init buffer with iChannel2 texture
    {
        if (distance(fragCoord, iResolution.xy * 0.5) < 50.)
            fragColor = vec4(0.8);
	}
    else if(iMouse.z > 0.0 && distance(fragCoord, iMouse.xy) < 100.0) 
    {
        fragColor = vec4(0);
    }
    else
    {
		fragColor = vec4(texture(iChannel1, uv * 0.5).rgb, 0.5);

        float _G0 = 0.215;//uSlider;
    	float _G1 = 0.15;//uSlider1;
    	float _G2 = 0.04;//uSlider2;

        // get adjacents cells from backbuffer 
        vec4 l = cell(fragCoord.xy, vec2(-1,0)); // left cell
        vec4 r = cell(fragCoord.xy, vec2(1,0)); // rigt cell
        vec4 t = cell(fragCoord.xy, vec2(0,1)); // top cell
        vec4 b = cell(fragCoord.xy, vec2(0,-1)); // bottom cell

        vec4 lt = cell(fragCoord.xy, vec2(-1,1)); // left top cell
        vec4 rt = cell(fragCoord.xy, vec2(1,1)); // right top cell
        vec4 lb = cell(fragCoord.xy, vec2(-1,-1)); // left bottom cell
        vec4 rb = cell(fragCoord.xy, vec2(1,-1)); // right bottom cell

        // get current cell from backbuffer
        vec4 c = cell(fragCoord.xy, vec2(0,0)); // current cell

        // quad dist from cells
		if (length((l+r+t+b)/4.-c) < 0.26)//uSlider3)
			fragColor = 
			_G0*c*length(c*c*c) + 
			+_G1*(l + r + t + b)
			+_G2*(lt + rt + lb + rb)
			;
	}

}
