void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float t = 0.;
    float d = 0.1;
    float c = 0.2;
    if (uv.x > sin(uv.y*10.+iTime))
    {
        t=sin(iTime)*0.5+0.5;
        d=tan(iTime)*0.5+0.5;
        c=cos(iTime)*0.5+0.5;
    }
	fragColor = vec4(t,d,c,0);
}
