void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float amp = 8.0;
    float freq = 2.0; //* abs(sin(iTime));
    float slide = 1.0 - iTime * 1.0;
    float xx = 1.0 - abs((fragCoord.y / iResolution.x - .3) * amp - sin((fragCoord.x / iResolution.x - slide) * freq));
    float xz = 1.0 - abs((fragCoord.y / iResolution.x - .3) * amp - sin((fragCoord.x / iResolution.x - 1.0 - slide * -.5) * freq));
    float xr = 1.0 - abs((fragCoord.y / iResolution.x - .3) * amp - sin((fragCoord.x / iResolution.x - 2.4 - slide * 1.9) * freq));
	fragColor.b = xz;
    fragColor.r = xx;
    fragColor.g = xr;
}
