#define ANTI 0.004
#define COLORFG vec3(0.58, 0.76, 0.28)
#define COLORBG vec3(1.00, 1.00, 1.00)
float box(in vec2 coord, in vec2 pos, in vec2 size)
{
    float d = length(max(abs(coord - pos) - size, 0.0));
    return smoothstep(0.0, ANTI, d);
}
float circle(in vec2 coord, in vec2 pos, in float radius)
{
    float d = length(coord - pos) - radius;
    return smoothstep(0.0, ANTI, d);
}
float hcircle(in vec2 coord, in vec2 pos, in float radius, in float off)
{
    vec2 q = coord - pos + vec2(0.0, off);
    float d = max(length(q) - radius, off - q.y);
    return smoothstep(0.0, ANTI, d);
}
float capsule(in vec2 coord, in vec2 a, in vec2 b, in float r) {
    vec2 pa = coord - a, ba = b - a;
    float h = clamp(dot(pa, ba)/dot(ba, ba), 0.0, 1.0 );
    float d =  length(pa - ba * h) - r;
    return smoothstep(0.0, ANTI, d);
}
float capsule2(in vec2 coord, in vec2 center, in vec2 polar, in float r)
{
    vec2 a = center + polar;
    vec2 b = center - polar;
    return capsule(coord, a, b, r);
}

float opUnion(in float a, in float b)
{
    return min(a, b);
}
float opSub(in float a, in float b)
{
    return max(a, 1.0 - b);
}

float android(in vec2 coord, in vec2 pos)
{
    float d = 1.0;

    vec2 bodysize = vec2(0.448, 0.402);
    d = opUnion(d, box(coord, pos, bodysize));//body

    {
        vec2 armoffset = vec2(0.158, 0.0);
        vec2 armpos = pos + vec2(-bodysize.x, 0.056) - armoffset;
        float harmlength = 0.3 - 0.104;
        d = opUnion(d, capsule(coord, 
                                    armpos + vec2(0.0, harmlength), 
                                    armpos - vec2(0.0, harmlength),
                                    0.104));//left arm

        armpos = pos + vec2(bodysize.x, 0.056) + armoffset;
        d = opUnion(d, capsule(coord, 
                                    armpos + vec2(0.0, harmlength), 
                                    armpos - vec2(0.0, harmlength),
                                    0.104));//right arm
    }

    vec2 headpos = pos + vec2(0.0, 0.468);
    float circleoff = 0.1;
    {
        d = opUnion(d, hcircle(coord, headpos, bodysize.x, circleoff));//head

        d = opSub(d, circle(coord, headpos + vec2(-0.196, 0.178), 0.054));//leye
        d = opSub(d, circle(coord, headpos + vec2( 0.196, 0.178), 0.054));//reye
    }
    {
        d = opUnion(d, capsule2(coord, pos + vec2(-0.18, -0.404), vec2(0.0, 0.328 - 0.1), 0.1));//lfeet
        d = opUnion(d, capsule2(coord, pos + vec2( 0.18, -0.404), vec2(0.0, 0.328 - 0.1), 0.1));//rfeet
    }
    {
        vec2 earsize = vec2(0.02, 0.10);
        vec2 earposl = vec2(0.0, bodysize.x - circleoff + earsize.y - 0.01);
        vec2 earposr = earposl;

        float theta = 45.0 / 180.0 * 3.14;
        earposl = mat2(cos(theta), sin(theta), -sin(theta), cos(theta)) * earposl;
        earposr = mat2(cos(theta), -sin(theta), sin(theta), cos(theta)) * earposr;

        d = opUnion(d, capsule2(coord, headpos + earposl, normalize(earposl) * earsize.y, earsize.x));
        d = opUnion(d, capsule2(coord, headpos + earposr, normalize(earposr) * earsize.y, earsize.x));
    }
    return d;
}

vec4 draw(in vec2 coord)
{
    float d = android(coord, vec2(0.0, -0.0));
    vec3 color = mix(COLORBG, COLORFG, smoothstep(0.0, ANTI, 1.0 - d));
    return vec4(color, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

	fragColor = draw(uv);
}
