#define PI 3.14159

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = ( fragCoord - .5 * iResolution.xy ) / iResolution.y;

    float beat = texture( iChannel0, vec2( 0.001, 0.25 ) ).x / 2.0;
    float dt = 1.5;//fract(iTime/5.)*2.;
    float time = PI*dt*2.;

	float cLength = length(uv)/(1.-beat);
    float offset = (uv.y/(cLength))*sin(cLength*PI*2.*4.-time)*4.;

    if(cLength <= (0.115+(dt*1.)/(PI*1.25)) && cLength >= (dt/(PI*1.75)))
    	fragColor = vec4(0.96, 0.96, 0.96, 1.0) + offset/32.;
    else
        fragColor = vec4(0.96, 0.96, 0.96, 1.0);
}
