float WIDTH = iResolution.x;
float HEIGHT = iResolution.y;
float offset = iTime * 60.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float x = fragCoord.x;
	float y = HEIGHT - fragCoord.y;


    float red   = x;
	float green = y + offset;
	float blue  = x + y;


	fragColor = fract(vec4(red, green, blue, 255.0) / 255.0);
}
