//Based on the code from https://www.shadertoy.com/view/ldXGRl
//Trying to learn shader mode :)

// speed of perturbations
const float PSPEED1 = 2.0;
const float PSPEED2 = 2.5;
const float PSPEED3 = 1.2;
// speed of flame cores
vec2 CORESPEED = vec2(-2.0, 2.0);
const vec3 color1 = vec3(0.4, 0.4, 0.8);
const vec3 color2 = vec3(0.8, 0.4, 0.4);
const vec3 color3 = vec3(-0.5, -0.5, -0.5);

float rand(vec2 n) { 
	return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);
}

float noise(vec2 n) {
	const vec2 d = vec2(0.0, 1.0);
	vec2 b = floor(n);
	vec2 f = smoothstep(vec2(0.0), vec2(1.0), fract(n));
	return mix(mix(rand(b), rand(b + d.yx), f.x), mix(rand(b + d.xy), rand(b + d.yy), f.x), f.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    vec2 pos = fragCoord.xy;
    float scale = (iResolution.y*2.0-fragCoord.y)*0.02;
    pos.y *= scale;
    pos.x *= -scale;
	vec2 p = pos.xy / iResolution.xy;
	vec2 r = vec2(noise(p - iTime*PSPEED1), 
                  noise(-p - iTime*PSPEED2));
	vec3 c = mix(color1, color2, noise(p + r + CORESPEED*iTime));
    vec2 r2 = vec2(0.0, 
                  noise(p - iTime*2.5));
    float yRatio = (fragCoord.y/iResolution.y); 
    float yRatioBtm = -1.5, 
          yRatioTop = 1.3 + sin(fragCoord.x*0.05-iTime*2.9)*0.2;
    yRatio = mix(yRatioBtm,yRatioTop,yRatio);
    float v = noise(p + r2 - vec2(3.0,5.3)*iTime*PSPEED3);
    c = mix(c, color3, v+yRatio);

    fragColor = vec4(c, 1.0);
}
