void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy/iResolution.xy-vec2(.5);
    uv *= pow((cos(iTime*.427345)*.5+.5),2.)+.04;
    uv *= 1.+texture(iChannel0,cos(uv)).x*(cos(iTime)*.5+.5)*.3;

    float k = 1.;
    vec3 c = vec3(1.),a = vec3(uv.x,k,uv.y);
    for (int i = 0; i < 8; i++) {
        k *= sign(uv.x+uv.y-1.);
        uv = mod(uv,vec2(.5))*2.;
        a = a.zxy;
        a += vec3(uv.x,k,uv.y);
    	c *= abs(a);
    }

    fragColor = vec4(c*k*.5+.5,1.);
}
