float ss(float mi,float ma,float f) { // sineSignal (min,max,alpha)
  float res;
	res = (sin(f)+1.)*(ma-mi)*0.5+mi;
  return res;
}

vec2 rot2d( in vec2 p,in float a) 
{
    vec2 res = p * mat2 ( cos(a), -sin(a), sin(a), cos(a) );
    return res;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = -1.0+2.*fragCoord.xy / iResolution.xy;
    p.x *= iResolution.x/iResolution.y;

    float z = abs(sin(iTime/1.3));
    vec4 c=texture(iChannel0,rot2d(p, cos(iTime) + ss(0.,2.4 , iTime*2. ) * z ) * z);

    fragColor = vec4(c);

}
