#define M_PI2 6.2831853
#define ACC 50.

void mainImage(out vec4 pixColor, in vec2 pixCoord) {
  float ratio = pixCoord.y / iResolution.y / 2.;
  float radius = iResolution.y / 3.;

  float timeLoop = mod(iTime, iResolution.x / ACC);

  vec2 circ = vec2(timeLoop * ACC, iResolution.y / 2. + sqrt(timeLoop) * ACC);

  float circleAdvance = circ.x / iResolution.x * 10.;

  float dist = length(pixCoord - circ);
  float wave = sin(pixCoord.x * M_PI2 / iResolution.x) * iResolution.y / 50. + iResolution.y / 2.;
  float advancedCirc = circleAdvance * .03;

    // TODO : antialiasing
    // TODO : Change colors

  //Sun
  if (dist <= radius && pixCoord.y >= wave) pixColor = vec4(advancedCirc + .1 + abs(dist / radius - 1.), advancedCirc + .2 + abs(dist / radius - 1.), advancedCirc + .8, 1.);
  //Skies
  else if (dist > radius && pixCoord.y > wave) pixColor = vec4(advancedCirc + .1, advancedCirc + .2, advancedCirc + .8, 1.);
  //Dune
  else pixColor = vec4(circleAdvance * .08 + ratio, circleAdvance * .04 + ratio, .05, 1.);
}
