void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float xDisplacement = 0.003 * sin(uv.x * 10.0 + iTime * 2.);
    vec2 warpedUV = vec2( uv.x + xDisplacement * sin(uv.y * 10.), uv.y);

    vec4 waterColor = vec4(1., 1., 1., 1.);
    float waterBrightness = (1. + sin(uv.x * 20. + iTime * 4.)) / 16.;


	fragColor = (waterBrightness * waterColor) + texture(iChannel0, warpedUV);
}
