// by Nikos Papadopoulos, 4rknova / 2015
// Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.

#define NO_COLOUR

void mainImage(out vec4 c, vec2 p)
{
	vec2 uv = .275 * p.xy / iResolution.y;
    float t = iTime*.03, k = cos(t), l = sin(t);        

    float s = .2;
    for(int i=0; i<64; ++i) {
        uv  = abs(uv) - s;    // Mirror
        uv *= mat2(k,-l,l,k); // Rotate
        s  *= .95156;         // Scale
    }

    float x = .5 + .5*cos(6.28318*(40.*length(uv)));
#ifdef NO_COLOUR
    c = vec4(vec3(x),1);
#else
    // Colouring based on Fabrice Neyret's code
    c = .5+.5*cos(7.47128*(40.*length(uv)*vec4(1,2,3,4)));
#endif
}
