void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	vec2 uv = fragCoord.xy / iResolution.xy - .5;

    float ang = atan(uv.y,uv.x);
    vec2 auv = vec2(0.,
                    length(uv)-.5);

    float d = 999.;
    for (int i = 0; i < 4; i++) {

        auv.x = mod(ang*float(i+1)+iTime*float(i),1.)-.5;

        float h = cos(iTime*2.+floor(ang) + cos(floor(ang*4.)+iTime)*.5 )*.1;
        d = min(d,
                length(max(abs(auv-vec2(0.,-.2+h))-vec2(.03,.1), 0.)));

    }

    d = max(0.,1.-d*100.);
    fragColor = vec4(d,d,d,1.);

}
