// version of https://www.shadertoy.com/view/ltSSDV -> https://www.shadertoy.com/view/MlSSDV

void mainImage( out vec4 o,  vec2 U ) {
    float t = iTime;
    U = -2.5 + ( 3. + sin( t / 2.7 ) ) * 3. * ( U / iResolution.xy - .5 );

    for (int i=0; i<8; i++)
    	U += ( 1. + cos( U.yx * 3. + vec2(t,1.6))) / 3.,
        U += ( 1. + sin( U.yx + t * 1.21 + vec2(1.6,0))) / 2.,
        U *= 1.3;

    vec3 c = vec3( U.xy, ( U.x - U.y ) );

	o.xyz = (.02*c-.3)*1.5; // color
}
