float sphere (vec2 p, vec2 m, float r)
{
    return max (0.0, distance (p, m) - r);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 screenPos = fragCoord.xy / iResolution.xy;
    float ratio = iResolution.y / iResolution.x;
    screenPos.x /= ratio;
    screenPos.x -= 0.5 * (iResolution.x - iResolution.y)
        / iResolution.y;

    int i;
    float s = 1.0;

    for (i = 0; i < 10; i++)
    {
        float fi = float (i);
        float x = 0.5 + 0.4 * cos (iTime * (0.1 + 0.1 * fi) + fi);
        float y = 0.5 + 0.4 * sin (iTime * (0.1 + 0.2 * fi) + fi);
        s = s * sphere (screenPos, vec2 (x, y), 0.125f);
    }

    fragColor =
          s > 0.001    ? vec4 (0.5, 0.5, 0.5, 1.0)
        : s > 0.0001   ? vec4 (0.2, 0.2, 0.2, 1.0)
        : s > 0.00001  ? vec4 (0.3, 0.2, 0.1, 1.0)
        : s > 0.000005 ? vec4 (0.7, 0.5, 0.1, 1.0)
        :                vec4 (0.0, 0.0, 0.0, 1.0);
}
