// http://lodev.org/cgtutor/juliamandelbrot.html
// http://warp.povusers.org/Mandelbrot/

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pos = -1.0+2.0*fragCoord.xy / iResolution.xy;
	pos.x *= iResolution.x/iResolution.y;

    vec2 c = pos;
    vec2 z = vec2(0.0, 0.0);
    vec4 col = vec4(0.0, 0.0, 0.0, 1.0);

    for (int i = 0; i < 50; i++)
    {
        z = vec2(z.x * z.x - z.y * z.y, 2.0f * z.x * z.y) + c;

        col.x = sin(z.x); 
        col.y = cos(z.y);
        col.z = tan(c.x * c.y);

        if (dot(z, z) > 4.0) 
            break;
    }

    fragColor = vec4(clamp(col.xyz, 0.0, 1.0), 0.0);
}
