void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    //fragColor = abs(texture(iChannel1,uv));//raw field
    fragColor = abs(texture(iChannel1,uv)-texture(iChannel0,uv));//delta
}

//Buf A - Save last field state for visualizing delta
#define FRAME_DELAY 8
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    if (iFrame%(FRAME_DELAY+1) > 0) {
     	fragColor = texture(iChannel1,uv);
        return;
    }
    fragColor = texture(iChannel0,uv);
}

//Buf B - Field simulation

//Try changing these parameters 
#define PARAM_A 0.5
#define PARAM_B -0.004
#define PARAM_C -0.5

//Make sure to change FRAME_DELAY in Buf A as well
#define FRAME_DELAY 8


//proper modulo, handles negative values correctly
float pmod(float v, float m) {
 	if (v < 0.) return m-mod(abs(v),m);
    return mod(v,m);
}

float hash(vec2 p) {
    return fract(dot(p+vec2(.36834,.723), normalize(fract(p.yx*73.91374)+1e-4))*7.38734);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //init
    if (iTime < 1. || (iMouse.w > 0. && length(iMouse.xy-fragCoord) < 30.)) {
        fragColor = texture(iChannel1,fragCoord/iChannelResolution[0].xy+sin(iDate.w*vec2(.03,cos(iDate.w*.1))));
     	return;
    }

    //frame delay
    if (iFrame%(FRAME_DELAY+1) > 0) {
     	fragColor = texture(iChannel0,fragCoord/iResolution.xy);
        return;
    }

    //load 3x3 field states
    vec2 uv = floor(fragCoord);
	vec4 s[9];
    for (int i = 0; i < 9; i++) {
        vec2 xyo = vec2(i%3-1,i/3-1);
        vec4 v  = texture(iChannel0,(vec2(pmod(uv.x+xyo.x,iResolution.x),pmod(uv.y+xyo.y,iResolution.y))+.5)/iResolution.xy);
        v.xy -= xyo/iResolution.xy;
        s[i] = v;
    }

    //randomize state order
    vec4 s0 = s[0], so;
    for (int i = 0; i < 9; i++) {
        int oi = 1+int(hash(uv+sin(iDate.w*vec2(.01,cos(iDate.w*.004)*.03))*4096.548+vec2(i/3,i%3)*45.7283)*7.99);
        so = s[oi];
        s[0] = so;
        s[oi] = s0;
        s0 = so;
    }

    //field process
    vec4 o = vec4(1);
    for (int i = 0; i < 9; i++) {
        vec4 v = s[i];
        o = mix(min(o,v.wzxy)+v*v.z*PARAM_A,min(o,v)+v.yxwz*v.w*PARAM_B,clamp(v.w-v.z*PARAM_C,0.,1.));
    }

    fragColor = clamp(o,-1.,1.);
}
