#define float4 vec4
#define frac fract
#define lerp mix


#define backgroundColor float4(0.0,0.0,0.0,1.0)


#define radius .3
#define width 0.2

bool IsInRange(float value,float pos,float range)
{
	return (value>= pos) && (value<= pos+range);
}

float SprialPattern(float2 uv)
{
	uv = uv*2.0 -1.0;

	float step = frac(length(uv)/radius);

    //bool b = IsInRange(step,length(uv),width);

    //if(b) return 1.0;

	//	//return 0.0;
	return step;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;    
    uv.x*=iResolution.x/iResolution.y; 
    uv.x -= 0.35;

    float value = SprialPattern(uv);

    float4 A = float4(0.6,0.6,0.1,1.0);
	float4 B = float4(.4,0.6,0.0,1.0);

    //float4 gc = smoothstep(A,B,float4(value,value,value,1.0));

	//float4 sprialPatternColor = float4(value,value,value,1.0);
	//float4 sprialPatternColor = gc;


	fragColor =  smoothstep(A,B,float4(value,value,value,1.0))*value;
}
