#define PI 3.14159265358979323846
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 pos = vec2(0.5)-uv;

    float angle = atan(pos.x,pos.y);
    float radius = length(pos)*2.0;

    float r =   sin(angle*10.0+sin(radius*PI*5.0)*10.0+
      iTime+sin(cos(sin(angle*30.0+iTime)*PI)*sin(cos(radius*20.0*PI)*PI)*0.4)
      *sin(radius*20.0+iTime)*PI*3.0)*1.0;



    float g = r*radius*0.5+sin(angle*10.0+sin(radius*PI*5.0)*10.0+iTime+sin(cos(sin(angle*30.0+iTime)*PI)*sin(cos(radius*20.0*PI)*PI)*0.4)*sin(radius*20.0+iTime)*PI*3.0)*0.5
    +cos(angle*10.0+sin(radius*PI*5.0)*10.0+iTime+sin(cos(sin(angle*30.0+iTime)*PI)*sin(cos(radius*20.0*PI)*PI)*0.4)*sin(radius*20.0+iTime)*PI*3.0)*0.5;
    float b = cos(angle*10.0+sin(radius*PI*5.0)*10.0+iTime+sin(cos(sin(angle*30.0+iTime)*PI)*sin(cos(radius*20.0*PI)*PI)*0.4)*sin(radius*20.0+iTime)*PI*3.0);

    fragColor = vec4(r,g,b,1.0);
}
