#define time iTime

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (2.0*fragCoord.xy-iResolution.xy) / min(iResolution.x,iResolution.y);

	uv /= .7 +abs(.7+length(uv)*(-2.0+sin(time))); // ball
    uv+=tan(time/5.); // slide

    vec3 c;
    c.r = smoothstep(0.,1., cos(uv.x*20.)+cos(uv.y*20.)+sin(time*10.));
    c.g = dFdy(c.r);
    c.b = dFdx(c.r);

    c.bg*=30.;
    fragColor = vec4(c,0);
}
