// variant of https://shadertoy.com/view/ll2fWG

void mainImage(out vec4 O, vec2 u) {
	vec2 R = iResolution.xy, r = floor(6.*R/R.y),
         U = (u+u-R)/R * r,  A = r-abs(U);
    if (A.x < A.y) U = vec2(.08-U.y*sign(U.x), r.y-A.x);
    U *= sign(U.y);
    float y = ceil(U.y);
    U = fract(U)-.5;
    O += abs( U.y *= 1.1 ) < .5
      ? U.y = dot( cos( 3.* y * max(0.,.5-length(U)) - vec2(33,0) ), U ),
	    smoothstep(1.,-1.,U/fwidth(U)).y
      : 2.*U.y; }
