// B&W version of colorful https://www.shadertoy.com/view/4l2yDD
// see 2-slopes version: https://www.shadertoy.com/view/MtByWm

#define S(v,s)  smoothstep( -2., 2., abs(o.x-v) * R.y - s )

void mainImage( out vec4 o,vec2 u)  {
    vec2 R = iResolution.xy, 
         U = (u+u-R)/R.y;    
    //float l = length(U);
    float l = dot(U,U) ;   

    float m = length(iMouse.xy)<10. ? .5 : iMouse.y/R.y; 
    o = vec4(l); if (l>.5) o -= m*(l-.5); 
    o = 1.-o;
    if (U.y<0.) o = pow(o,vec4(1./2.2)) ;        // bottom: sRGB conversion
}
