// hue/saturate/value
vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy / iResolution.xy - 0.5) * 2.0;

    float angle = atan(uv.y, uv.x);
    float len = length(uv);
    float ti = iTime * 0.1;

    float l1 = sin(angle + ti * 1.284) * 0.25;
    float l2 = -sin(angle * 3.+ ti * 7.8643) * 0.25;
    float l3 = -cos(angle * 15.+ ti * 1.582) * 0.25;
    float l4 = cos(angle * 7.+ ti * 7.73) * 0.25;

    len += (l1 + l2 + l3 + l4) * 0.5;

    len = max(len, 0.);

    float s = smoothstep(len + 0.25, 0., 0.5) * 1.0;

    fragColor = vec4(hsv2rgb(vec3(sin(angle * 3. + ti * 3.0) + cos(s - ti * 1.5), 0.5, 1.0)), 1.0); 
}
