vec3 COLOR_1 = vec3(0.26, 0.7, 0.65);
vec3 COLOR_2 = vec3(0.45, 0.6, 0.55);

vec3 SCALE_1 = vec3(0.0638, -0.2, 0.0);
vec3 SCALE_2 = vec3(0.2638, -0.15, 0.0);

float TIME_SCALE = 3.0;
float TIME_AMPLITUDE = 3.0;

float sRGB(float t){ return mix(1.055*pow(t, 1./2.4) - 0.055, 12.92*t, step(t, 0.0031308)); }
vec3 sRGB(in vec3 c) { return vec3 (sRGB(c.x), sRGB(c.y), sRGB(c.z)); }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	
	vec2 uv = fragCoord.xy / iResolution.xy;

    float tx = (uv.x - 0.5) * cos(iTime * TIME_AMPLITUDE) * TIME_SCALE;
    float ty = (uv.y - 0.5) * sin(iTime * TIME_AMPLITUDE) * TIME_SCALE;
    vec3 col1 = COLOR_1 + SCALE_1 * vec3(tx, ty, 1.0);
    vec3 col2 = COLOR_2 + SCALE_2 * vec3(ty, tx, 1.0);

    vec3 col = mix(col2, col1, uv.x * uv.y);

    col = sRGB(col);

	fragColor = vec4( col, 1.0 );   
}
