void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    float pxByUnit = iResolution.y / 4.0;
    vec3 center = vec3(0.0, 0.0, 0.0);
    vec3 light1 = vec3(0.5, 1.0, -1.0);
    vec3 light2 = vec3(0.5, 1.0, -2.0);

    float lastPointZ = 100.0;


    fragColor = vec4(1, 1, 1, 1);

    for (float z = -1.0; z <= 1.0; z += 0.001) {
        vec3 worldCoord = vec3(((fragCoord.x - iResolution.x / 2.0) / pxByUnit), ((fragCoord.y - iResolution.y / 2.0) / pxByUnit), z);
		float ray = distance(worldCoord, center);

        if (ray <= 1.0 && lastPointZ > z) {
	        float lightness = distance(worldCoord, light1) * -0.6  + 1.0;


            fragColor = vec4(0.2 * lightness, 0.6 * lightness, 0.86 * lightness, 1);
            lastPointZ = z;
        }
    }

}
