void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	//fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);


    //red line down the middle
    float bluegreen = 1.0 - (uv.x * 2.0 ) ;
    float magMult = -1.999;

    vec2 uvBase = uv;
		uvBase[0] -= 0.5;
		//uvBase[1] -= 0.5;
		float mag = (((((uvBase[0] * uvBase[0])  )  *magMult ) * 150.0) * magMult );

    	//this helps cause the harsh difference in red gradient
		if (mag > 1.5) { mag = 1.9; }
    	else {mag = mag * 0.15;}
   		if (uv.x > 0.5) {mag = -mag;}

    bluegreen = bluegreen * mag *1.5;



    fragColor = vec4 (1,bluegreen,bluegreen,1);


 	//black cutout area

    //float cutoutArea = cos( (1.0 +uv.x * 0.5-uv.x)) ;

    float cutoutArea = 1.0;
    int points = 10;

    // this is very messy here and only works for this example (non dynamic)
    float currentPoint = (float(points) * 0.1 ) * ( 1.75 + sin(  5.46 - uv.x * 0.5 - uv.x));


    cutoutArea = currentPoint;

        if (uv.y >= cutoutArea) 
        {
             fragColor = vec4 (0,0,0,1);
        }

}
