void mainImage( out vec4 o, in vec2 q )
{       
    vec2 u = q/iResolution.xy;

    float l = dot(texture(iChannel1,u).rgb,vec3(0.2125,0.7154,0.0721)); //luminocity

    //get polar coordinates
  	u -= .5;
    float r = length(u);
    float phi = atan(u.y,u.x);

    //apply displacement
    float v = 1.;  //one of means to control background homogenosity
				   //bigger values -> more same tiles -> more same pixels -> less visible figure
    //v = iTime*0.1;
    r *= l+v;

    //back to cartesian coordinates
    u = vec2(.5);
    u.x += r*cos(phi);
    u.y += r*sin(phi);

 	//u += iTime*.1; //move background, another method to control homogenosity

    o = texture(iChannel0,u);
}
