//uncomment to disable timely fade
//otherwise the mouse will control the intensity
#define TimeFade

//uncomment to colorize vignette
#define UseColor

//vignette color modifier, if enabled, in RGB (0.0-1.0)
#define Color vec3(0.25, 0.0, 0.125)

//uncomment to disable gamma correction
#define UseGammaCorrection

//uncomment to use a white background
//#define Debug

void mainImage( out vec4 fragColor, in vec2 uv) {
	uv /= iResolution.xy;
    vec3 col = vec3(1.0);

    float mouse = iMouse.x / iResolution.x;

    #ifdef TimeFade
    float amount = (1.0 + 0.5 * sin(iTime));
    #else
    float amount = mouse;
    #endif

    #ifndef Debug
    col = texture(iChannel0, uv).rgb;
    #endif

    #ifdef UseGammaCorrection
    col = pow(col, vec3(1.0 / 2.2));
    #endif

    float vignette = distance(uv, vec2(0.5));
    vignette = mix(1.0, 1.0 - amount, vignette);

    #ifdef UseColor
    col = mix(col * Color, col, vignette);
    #else
    col *= vignette;
    #endif

    #ifdef UseGammaCorrection
    col = pow(col, vec3(2.2));
    #endif

	fragColor = vec4(col, 1.0);
}
