void mainImage(out vec4 color, in vec2 coord){
    const vec4 fg = vec4(0.08, 0.07, 0.08, 1.0);
    const vec4 bg = vec4(0.5, 0.14, 0.1, 1.0);
    vec2 uv = coord / iResolution.xy;
    float t = iTime * 1.2;
    float t1 = fract(t);
    if(sin(t + t1 * 3.0 + tan(coord.y * 0.05) - sqrt(coord.y * t1)) >= 0.7){
        float y = uv.y * 12.0;
        if(y - floor(y) >= 0.5){
            uv.x += 0.06 * t1;
        }else{
            uv.x -= 0.06 * (1.0 - t1);
        }
    }
    bool swap = t1 >= 0.5;
    bool source = texture(iChannel0, uv).g > 0.4;
    vec4 col1 = swap ? fg : bg;
    vec4 col2 = swap ? bg : fg;
    color = source ? col1 : col2;
}
