#define v vec2
float s(v a, v b)
{
    return length( a - b*dot(a,b)/dot(b,b) ) * .1 -.01;
}

void mainImage( out vec4 O, v C )
{
    v A = v(-.9, -.6), B = v(0, .9), D = v(.9, -.6),
        R = iResolution.xy,
        p = (C+C - R) / R.y;

	O = vec4(max(
        	(length(p * v(2, 5)) - 1.) // ellipse
       		 * (1. - length(p) * 5.) // circle
        	+ sin(iTime)* .13,
        	s(p - A, B - A) + s(p - A, D - A) + s(p - B, D - B) // triangle
            ) 
        );
}
