void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	//vec3 newColor = texture(iChannel0, uv).xyz;
    vec4 textureColor = texture(iChannel0, uv);

    float red = textureColor.r;
    float green = textureColor.g;
    float blue = textureColor.b;

    float final = (red + green + blue) / 3.0;

    fragColor = vec4(final, final, final, 1.0);
}
