float star(vec2 p, float arms)
{
    return dot(p, p) * (cos(atan(p.y, p.x) * arms) * .33 + .67);// shane formula
}

float df(vec2 p, vec2 id)
{
    float idx = (id.x + id.y) * 3.;
    return star(p, idx);
}

void mainImage( out vec4 fC, vec2 fG )
{
	vec2 p = fG /= iResolution.y / 10., cid;

    float d = 9.;

    for(int x=-3;x<=3;x++)
    for(int y=-3;y<=3;y++)
    {	
        p = vec2(x,y);
        vec2 id = fract(sin((floor(fG)+p)*mat2(2,5,5,2)));
        vec2 a = sin( iTime * vec2(1,.5) + 9. * id );
		p += .5 + .35*a - fract(fG);
        p *= mat2(cos(a.x),-sin(a.x),sin(a.x),cos(a.x)) * cos(a.y*1.5) * 5.;
       	float s = df(p,id);
        if (s < d)
        {
            d = s;
            cid = id;
        }
    }

    vec3 col = vec3(cid.x, cid.y, sin(cos(cid.x + cid.y))) * d;

    fC = sqrt(sqrt(vec4(col,1)));
}
