// based on http://glslsandbox.com/e#38142.5
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float halfpi = atan(-1.0)*-2.0;
	vec2 s = iResolution.xy;
	vec2 g = gl_FragCoord.xy;
	vec4 h = texture(iChannel0, g / s);
	g = (fragCoord * 2.-s)/s.y*2.5;
    float prec = 0.005;
	float color = 0.0;
	for (int i = -7; i < 8; ++i)
	{
		vec2 
        k = vec2(halfpi + float(i) * 0.1,0) + 
            mod(iTime * 0.1 * (float(i) + 0.4) * 0.2, 4.0 * halfpi), 
		a = mod(g - sin(k + k * log(k)*0.9),g)-g*.5,
	    b = mod(g - sin(2.09 + k * sin(k)*0.8),g)-g*.5,
	    c = mod(g - sin(4.18 + k * cos(k)*0.7),g)-g*.5;
		color += (prec/dot(a,a) + prec/dot(b,b) + prec/dot(c,c));
	}
	fragColor = color * 0.001 + h * 0.97 + step(h, vec4(.8,.2,.5,1)) * 0.01;
}
