#define boxradius 0.9
#define boxdarkness 0.75
#define desat 0.85
#define black vec3(0.0,0.0,0.0)
#define sepia vec3(1.0,0.95,0.9)

#define halfsies

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	vec2 uv = fragCoord.xy / iResolution.xy;
    // uv goes from 0 to 1

    vec4 ch0 = texture(iChannel0,uv);

    #ifdef halfsies

    if (uv.x < 0.5){
        fragColor = ch0;
    	return;
    }

    #endif

	fragColor = ch0 * ch0 * 1.4;// add some grit, overexposure
    float lum = (fragColor.r + fragColor.g + fragColor.b) /3.0;
	fragColor.rgb = fragColor.rgb * (1.0 - desat) + sepia * vec3(lum,lum,lum) * desat;

   // shadowbox it
   float r = distance(vec2(0.5,0.5),uv)*2.0;
   // r is 0 to 1 for the radius from center
    if (r > boxradius){
  		fragColor.rgb = mix( fragColor.rgb, black, (r - boxradius)*boxdarkness);
    }
}
