void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float drunkAmount = 2.0;

    vec2 uv = fragCoord/iResolution.xy;

    vec2 d = 0.5 - uv;
	//d *= drunkAmount;
    d.x += 0.05 * sin(iTime) * (0.15 + drunkAmount);
    d.y += 0.05 * cos(iTime/1.2) * (0.15 + drunkAmount);

    vec2 coords = uv;

    	coords += vec2(sign(d.x) * abs(pow(d.x, drunkAmount * 4.0)), sign(d.y) * abs(pow(d.y, drunkAmount * 4.0)));
    	coords.x = clamp(coords.x, 0.0, 1.0);
    	coords.y = clamp(coords.y, 0.0, 1.0);
    vec3 col = texture(iChannel0, coords).rgb;

    vec2 dCoords = uv + 0.05 * d;
    dCoords.x += 0.01 * sin(iTime) * (0.05 + drunkAmount);
    dCoords.y += 0.01 * cos(iTime/1.2) * (0.05 + drunkAmount);

    vec3 dCol = texture(iChannel0, dCoords).rgb;
    col = (1.5 * col + 0.5 * dCol)/2.0;


    // Output to screen
    fragColor = vec4(col,1.0);
}
