void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy; 
 // U *= .5; R*= .5;  // uncomment for zoomed version

    O = U.x<R.x/2. ? texelFetch(iChannel0, ivec2(mod(U,iChannelResolution[0].xy)),0)
                   : texelFetch(iChannel1, ivec2(mod(U,iChannelResolution[1].xy)),0);

    O = O.xxxx;       // comment if you want colors

    if (U.y>R.y/2.) O = fract( O + iTime ); 

    if (abs(U.x-R.x/2.)>R.x/4.) O = step(.5,O);
}
