vec2 R;
vec4 T ( vec2 U ) {return texture(iChannel0,U/R);}
vec4 P ( vec2 U ) {return texture(iChannel2,U/R);}
void mainImage( out vec4 C, in vec2 U )
{
    R = iResolution.xy;
    C = P(U);
    C = 1.5*C+vec4(C.w,0,0,0);
    //C = 10.*C.wwww;
}

vec2 R;
vec4 T ( vec2 U ) {return texture(iChannel0,U/R);}
float X (vec2 U, vec2 u, inout vec4 Q, in vec2 r) {
    vec2 v = T(U+r).xy;
    float P = T (U+r-v).z;
    Q.xy -= 0.25*r*(P-Q.z);
    return (0.5*(length(r-v+u)-length(r+v-u))+P);
}

void mainImage( out vec4 Q, in vec2 U )
{   R = iResolution.xy;
 	vec2 u = T(U).xy, e = vec2(1,0);
 	float P = 0.; Q = T(U-u);
 	Q.z = 0.25*(
       X (U,u,Q, e)+
 	   X (U,u,Q,-e)+
 	   X (U,u,Q, e.yx)+
 	   X (U,u,Q,-e.yx));
 	//init
 	if (iFrame < 1) Q = vec4(0);
 	//boundaries
    if (U.x < 1.||U.y < 1.||R.x-U.x < 1.||R.y-U.y < 1.) Q.xy *= 0.;
 	//jets
 	if (length(U-vec2(0.1,0.5)*R) < 9.) 
        Q.xy= Q.xy*.9+.1*vec2(.5,0);
 	if (length(U-vec2(0.7,0.3)*R) < 9.) 
        Q.xy= Q.xy*.9+.1*vec2(-.6,.7);
}

// Voronoi based particle tracking

vec2 R;float N;
vec4 T ( vec2 U ) {return texture(iChannel0,U/R);}
vec4 P ( vec2 U ) {return texture(iChannel1,U/R);}
void swap (vec2 U, inout vec4 Q, vec2 u) {
    vec4 p = P(U+u);
    float dl = length(U-Q.xy) - length(U-p.xy);
    float e = .1;
    // allows for probabistic reproduction
    Q = mix(Q,p,0.5+0.5*sign(floor(1e2*dl+0.5)));
}
void mainImage( out vec4 Q, in vec2 U )
{   R = iResolution.xy;
 	U = U-T(U).xy;
 	Q = P(U);
 	swap(U,Q,vec2(1,0));
 	swap(U,Q,vec2(0,1));
 	swap(U,Q,vec2(0,-1));
 	swap(U,Q,vec2(-1,0));

 	if ((length(U-vec2(0.1,0.5)*R) < 4.))
        Q.zw = vec2(0,3);
    if ((length(U-vec2(0.7,0.3)*R) < 4.))
        Q.zw = vec2(-2,2); 

 	Q.xy = Q.xy + T(Q.xy).xy;
 	if (iFrame < 1) Q = vec4(U,0,0);
}

//Render particles
vec2 R;
vec4 T ( vec2 U ) {return texture(iChannel0,U/R);}
vec4 P ( vec2 U ) {return texture(iChannel1,U/R);}
void mainImage( out vec4 C, in vec2 U )
{	R = iResolution.xy;
    C = P(U);
	vec2 
        n = P(U+vec2(0,1)).xy,
        e = P(U+vec2(1,0)).xy,
        s = P(U-vec2(0,1)).xy,
        w = P(U-vec2(1,0)).xy;

 	C = vec4(0.1+0.5*(sin(.4*C.z*vec3(2)+.6*C.w*vec3(1,2,3)))*smoothstep(2.,0.,length(C.xy-U)),
       (length(n-C.xy)-1.+
        length(e-C.xy)-1.+
        length(s-C.xy)-1.+
        length(w-C.xy)-1.)/R.y*10. 
            );
 	if(iFrame < 1) C = vec4(0);
}
