void mainImage( out vec4 f, vec2 g )
{
    f = vec4(0.0);
    f.xyz = iResolution;
    vec2 v = (g+g-f.xy)/f.y*1.,k;
    vec4 base = (texture(iChannel0, g/f.xy) / length(f)) ;
    f *= base * 2.0;
   	g = vec2(iFrame);
    k = v - sin(g + vec2(1.6,0)) * fract(iTime*.1+4.52)*3.;
    vec4 check = vec4((1e-2 / dot(
        k,
        k)));
    f += check * vec4(vec3(0.2,0.9,0.4) * check.b, check.a);
}
