#ifdef GL_ES
precision highp float;
#endif

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    float border = 0.004;
    vec3 circle_color = vec3 (1.0,1.0,1.0);
    vec2 p = fragCoord.xy / iResolution.xy;
    vec2 c = p - vec2 (0.2, 0.5);

    float rot = iTime * 2.2;
    vec3 col = vec3 (0.0);

    vec2 prevc;
    //vec2 center = vec2 (0.5,0.5); 

    float radius = 0.20;
    float prevdist = 0.0;
    vec2 center = vec2(0.2 + 14.0*0.03 + 0.15 * sin(14.0 * 0.5 + rot/2.0),0.5 + 0.1 * cos(14.0 * 0.5 + 1.67 + rot));     



    for (int i = 0 ; i < 15 ; i++) { ; 
    vec2 plot = vec2(0.2 + float(i)*0.03 + 0.15 * sin(float(i) * 0.5 + rot/2.0), 
                    0.5 + 0.1 * cos(float(i) * 0.5 + 1.67 + rot));   
                               c = p - plot;

   float dist =  sqrt(dot(c, c));
   float prevdist = distance(prevc,c);
   float pdist = distance(center,p);                             

 if ( (dist > (radius)) && (dist < (radius+border)) && (pdist < radius) || (pdist > (radius)) && dist < (radius+border) )
  //  if ( (dist > (radius)) && (dist < (radius+border)) )
    col = circle_color*(float(i)*(1.0/14.0));

   prevc = c;
                               }
   float f = smoothstep (0.01, 0.011, distance(p, center));
   col += 1.0- vec3(1.0) *f;//plot center of first circle
   fragColor = vec4(col,1.0);

}
