//an edit of shader found at http://glslsandbox.com/e#20524.3

#ifdef GL_ES
precision mediump float;
#endif

vec2 resolution;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = iTime;
	vec2 a; 
	a.xy = iResolution.yx * 0.0245; 	
    vec2 p = ( fragCoord.xy / a.xy ) * 0.169125;
	vec2 q = vec2( 1.0-cos(p.x), 1.0-sin(p.y) );
	time = time*2.0 + q.x / q.y / length( q );

	vec3 c = 1.0-vec3( 1.05 );

	c += vec3(0.10, 0.5, 0.05) * fract( (              p.x + p.y + sin(time*0.5) ) * 0.54 ) ;
	c *= vec3(0.5, 2.0, 0.5) * fract( (sin(time*0.0005)*p.x - cos(time*0.015)*p.y + fract(time*0.005) ) * 15.0 ) ;
	c *= dot(p.x/p.y,( p.x * p.y ));
    c -= 1.0-vec3(0.10,0.25,0.175)*vec3(p, 0.95);

    float fd = 1.0-(p.y+p.x);
	fragColor = vec4( c+fd, 1.0 );
}
