void mainImage( out vec4 f, in vec2 w ) {
    vec4 p = vec4(w,0.,1.)/iResolution.xyxy-.5, d=p, t, c;
    p.x += iTime;
    for(float i=1.; i>0.; i-=.02)
    {
        t = abs(mod(p, 8.)-4.);
        c = texture(iChannel0, t.zxy-5.);
        float x = -log(exp(-t.y) + exp(-length(t.xz)))-c.x; 
        f = mix(c*i*i, vec4(0,1,2,0), p.z*.01);
        if(x<.01) break;
        p -= d*x;
     }
}
