vec2 col;
float startoffset = 2000.0;

void innercolor(){col = vec2(0.9 ,0.5*cos(iTime));}
void outercolor(){col = vec2(0.6,0.6*sin(iTime));}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy; // get 2d plane
    uv.x *= iResolution.x/iResolution.y; //fix aspect ratio

    uv.x -= 1.0; //move right
    uv.y -= 0.5; //move up
    //uv.x += 0.4; //move left
    //uv.y += 0.4; //move down

    float radius = 0.1;

    ///plot color values

    float value = cos((atan(uv.y,uv.x)*(startoffset+iTime)/200.0)/cos(iTime/4.0))*8.0
        + sin((atan(uv.y,uv.x)*(startoffset+iTime)/2000.0)/cos(iTime/8.0))*2.0;

    if(value < radius) outercolor();
    if(value >= radius)innercolor();

    ///draw output
	fragColor = vec4(col,
                        cos(iTime) / 2.0
                        ,1.0);
}
