#define PI 3.14159265359
#define PI2 2.*PI
#define HPI PI/2.

//moving
//#define BIAS sin(iTime)*.5+.5
//#define BIAS .9
#define BIAS texture(iChannel0,vec2(1. - iMouse.x/iResolution.x,0.)).x

//#define GAIN iMouse.x/iResolution.x
#define GAIN texture(iChannel0,vec2(iMouse.x/iResolution.x,0.)).x
//#define GAIN .5

float GetBias(float x,float bias)
{
  return (x / ((((1.0/bias) - 2.0)*(1.0 - x))+1.0));
}

float GetGain(float x,float gain)
{
  if(x < 0.5)
    return GetBias(x * 2.0,gain)/2.0;
  else
    return GetBias(x * 2.0 - 1.0,1.0 - gain)/2.0 + 0.5;
}


float drawFunc(vec2 uv,float f){
    return step(f -.025,uv.y) * step(uv.y, f + 0.025);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	float strock =.01;

    float x = uv.x; // [-1.0,1.0]

    float b = GetBias(x,BIAS);

    float g = GetGain(x,GAIN);

    b = sin(b * PI2) *.9;
    g = sin(g * PI2) *.9;

    float f;
    uv.y  = uv.y * 2. -1.;
	f = drawFunc(uv,b) + drawFunc(uv,g);
	fragColor = vec4(vec3(1.),1.) * f;
}
