mat3 xrot(float t)
{
    return mat3(1.0, 0.0, 0.0,
                0.0, cos(t), -sin(t),
                0.0, sin(t), cos(t));
}

mat3 yrot(float t)
{
    return mat3(cos(t), 0.0, -sin(t),
                0.0, 1.0, 0.0,
                sin(t), 0.0, cos(t));
}

mat3 zrot(float t)
{
    return mat3(cos(t), -sin(t), 0.0,
                sin(t), cos(t), 0.0,
                0.0, 0.0, 1.0);
}

float plane(vec3 p, float b) {
    return b - abs(p.z);
}

float sdTubeXY(vec3 p, float b)
{
 	return length(p.xy) - b;   
}

float sdBoxXY(vec3 p, float b)
{
  vec2 d = abs(p.xy) - b;
  return min(max(d.x,d.y),0.0) +
         length(max(d,0.0));
}

float sdBoxXZ(vec3 p, float b, float c)
{
  return length(max(abs(p.xz)-vec2(b,c),0.0));
}

float sdBoxYZ(vec3 p, float b, float c)
{
  return length(max(abs(p.yz)-vec2(b,c),0.0));
}

vec3 camPos()
{
    float pz = iTime * 2.0;
 	return vec3(0.0, 0.0, pz);   
}

float map(vec3 p)
{
    vec3 q = fract(p) * 2.0 - 1.0;
    vec3 f = floor(p);
    vec3 cp = camPos();
    float c = 4.1;
    float m = mod(f.z, 4.0);
    float d = 0.0;
    float da = 0.5 + 0.5 * sin(iTime);
    float dd = mix(3.0, 8.0, da);
    float am = clamp(dd - abs(cp.z - f.z - 1.0), 0.0, 1.0);
    float at = 1.0 / (1.0 + abs(cp.z - f.z - 1.0) * 0.01);
    float anim = mix(0.0, at, am);
    float bs = mix(8.0, 2.0, anim);
    float cs = 0.5;
    if (m == 0.0) {
        vec3 r = vec3(p.x+c, p.y, q.z);
        d = sdBoxXZ(r, bs, cs);
    } else if (m == 1.0) {
        vec3 r = vec3(p.x, p.y+c, q.z);
        d = sdBoxYZ(r, bs, cs);
    } else if (m == 2.0) {
        vec3 r = vec3(p.x-c, p.y, q.z);
        d = sdBoxXZ(r, bs, cs);
    } else {
        vec3 r = vec3(p.x, p.y-c, q.z);
        d = sdBoxYZ(r, bs, cs);
    }
    float im = plane(q, 1.3);
    float sr = min(d, im);
    float tn = sdBoxXY(p, 2.5);
	return min(sr, -tn);
}

vec3 normal(vec3 p)
{
	vec3 o = vec3(0.01, 0.0, 0.0);
    return normalize(vec3(map(p+o.xyy) - map(p-o.xyy),
                          map(p+o.yxy) - map(p-o.yxy),
                          map(p+o.yyx) - map(p-o.yyx)));
}

float trace(vec3 o, vec3 r)
{
	float t = 0.0;
    for (int i = 0; i < 32; ++i) {
        vec3 p = o + r * t;
        float d = map(p);
        t += d * 0.5;
    }
    return t;
}

vec3 textex(vec3 p)
{
    vec3 ta = texture(iChannel0, vec2(p.y,p.z)).xyz;
    vec3 tb = texture(iChannel0, vec2(p.x,p.z)).xyz;
    vec3 tc = texture(iChannel0, vec2(p.x,p.y)).xyz;
    return (ta + tb + tc) / 3.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    vec3 r = normalize(vec3(uv, 1.0 - dot(uv,uv)));
    r *= yrot(3.14159*0.5);

    vec3 o = camPos();
    o.x -= 1.0;
    o.z += cos(iTime*2.0);

    float t = trace(o, r);
    vec3 w = o + r * t;
    vec3 sn = normal(w);
    float fd = map(w);

    float prod = max(dot(sn, -r), 0.0);

    float fog = prod / (1.0 + t * t * 0.1);

    vec3 diff = textex(w*0.25);

    diff = mix(diff, diff*vec3(1.0), 0.5+0.5*sn.y);
    diff = mix(diff, vec3(0.0), 0.5-0.5*sn.y);
    diff = mix(diff, vec3(1.0), abs(sn.z));
    diff = mix(diff, diff*vec3(0.25), abs(sn.x));

    vec3 fc = diff * fog;

	fragColor = vec4(sqrt(fc), 1.0);
}
